import {CST} from "../CST";

export class Game extends Phaser.Scene{
    constructor(){
        super({
            key:CST.SCENES.GAME
        })
    }
    create(){
        var Lamy = this.add.image(this.game.renderer.height / 2, this.game.renderer.width / 2, "Lamy");
        this.keyboard = this.input.keyboard.addKeys("A, D");
    }

    update(){
        if(this.keyboard.A.isDown === true){
            Lamy.rotation += 0.01;
        }
    }
}
